import axios from "axios";
import moment from "moment";
import React, { useCallback, useEffect, useState } from "react";
import {
  Button,
  Card,
  Container,
  Form,
  Placeholder,
  Stack,
} from "react-bootstrap";
import InfiniteScroll from "react-infinite-scroll-component";
import sortBy from "lodash";

const Home: React.FC = () => {
  const [title, setTitle] = useState();
  const [content, setContent] = useState();

  const [posts, setPosts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const handleOnChange = useCallback(
    (event, prop) => {
      if (prop === "title") {
        setTitle(event.target.value);
      }
      if (prop === "content") {
        setContent(event.target.value);
      }
    },
    [title, content]
  );
  const now = moment().format("DD-MM-YYYY");
  console.log(now);
  const onSubmit = () => {
    axios
      .post("http://localhost:8080/create-feed", {
        title,
        content,
      })
      .then((response) => {
        if (response && response.data && response.data.success) {
          console.log("response: ", response);
        }
      })
      .catch(console.warn);
  };

  useEffect(() => {
    const fetchFeeds = async () => {
      await axios
        .get("http://localhost:8080/")
        .then((response) => {
          console.log("response1", response);
          if (response && response.data && response.data.success) {
            const sortPost = sortBy(response.data.payload, "createdAt");
            setPosts(sortPost);
            setIsLoading(false);
          }
        })
        .catch(console.error);
    };
    fetchFeeds();
  }, []);
  console.log("posts: ", posts);
  const fetchMoreData = () => {};
  return (
    <div>
      <h1 className="mt-3">Assignment</h1>
      <Container>
        <Form className="mb-3">
          <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
            <Form.Label>Title</Form.Label>
            <Form.Control
              value={title}
              type="text"
              placeholder="Enter Title"
              onChange={(e) => handleOnChange(e, "title")}
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
            <Form.Label>Content</Form.Label>
            <Form.Control
              onChange={(e) => handleOnChange(e, "content")}
              required
              value={content}
              as="textarea"
              rows={3}
            />
          </Form.Group>
          <Button onClick={onSubmit} variant="warning">
            Submit Content
          </Button>{" "}
        </Form>
      </Container>
      <Container>
        <h1>Posts</h1>
      </Container>
      <Container>
        <InfiniteScroll
          dataLength={8}
          next={fetchMoreData}
          hasMore={true}
          loader={<h4>Loading...</h4>}
        >
          {/* {this.state.items.map((i, index) => (
            <div style={style} key={index}>
              div - #{index}
            </div>
          ))} */}
          <Stack gap={4} className="col-md-4 mx-auto">
            {
              <Card style={{ width: "28rem" }}>
                <Card.Body>
                  <Card.Title>Card Title</Card.Title>
                  <Card.Text>
                    Some quick example text to build on the card title and make
                    up the bulk of the card's content.
                  </Card.Text>
                </Card.Body>
              </Card>
            }
          </Stack>
          {/* <Card style={{ width: "18rem" }}>
            <Card.Body>
              <Placeholder as={Card.Title} animation="glow">
                <Placeholder xs={6} />
              </Placeholder>
              <Placeholder as={Card.Text} animation="glow">
                <Placeholder xs={7} /> <Placeholder xs={4} />{" "}
                <Placeholder xs={4} /> <Placeholder xs={6} />{" "}
                <Placeholder xs={8} />
              </Placeholder>
              <Placeholder.Button variant="primary" xs={6} />
            </Card.Body>
          </Card> */}
        </InfiniteScroll>
      </Container>
    </div>
  );
};

export default Home;
