import { Button, Container, Form } from "react-bootstrap";
import InfiniteScroll from "react-infinite-scroll-component";

export default function Home() {
  const fetchMoreData = () => {};
  return (
    <div>
      <h1 className="mt-3">Assignment</h1>
      <Container>
        <Form className="mb-3">
          <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
            <Form.Label>Title</Form.Label>
            <Form.Control type="text" placeholder="name@example.com" />
          </Form.Group>
          <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
            <Form.Label>Content</Form.Label>
            <Form.Control as="textarea" rows={3} />
          </Form.Group>
          <Button variant="warning">Submit Content</Button>{" "}
        </Form>
      </Container>

      <Container>
        <InfiniteScroll
          dataLength={8}
          next={fetchMoreData}
          hasMore={true}
          loader={<h4>Loading...</h4>}
        >
          {/* {this.state.items.map((i, index) => (
            <div style={style} key={index}>
              div - #{index}
            </div>
          ))} */}

          <div>
            <h4>King Kong</h4>
            <p>
              Lorem ipsum dolor sit amet consectetur adipisicing elit. Atque
              eius animi placeat qui sequi sunt minima quas corrupti quisquam,
              repellat dicta expedita porro quos distinctio fugiat aperiam vero
              voluptas dolores.
            </p>
          </div>
        </InfiniteScroll>
      </Container>
    </div>
  );
}
