import axios from "axios";
import moment from "moment";
import React, { useCallback, useEffect, useState } from "react";
import { Button, Card, Container, Form, Spinner, Stack } from "react-bootstrap";
import InfiniteScroll from "react-infinite-scroll-component";

const Home: React.FC = () => {
  const [title, setTitle] = useState<string>();
  const [content, setContent] = useState<string>();
  const [page, setPage] = useState<number>(1);
  const [posts, setPosts] = useState<any[]>();
  const [totalPosts, setTotalPosts] = useState<number>(0);
  const [isLoading, setIsLoading] = useState(true);

  const handleOnChange = useCallback(
    (event, prop) => {
      if (prop === "title") {
        setTitle(event.target.value);
      }
      if (prop === "content") {
        setContent(event.target.value);
      }
    },
    [title, content]
  );
  const createdAt = moment().unix();
  const onSubmit = () => {
    axios
      .post("http://localhost:8080/create-feed", {
        title,
        content,
        createdAt,
      })
      .then((response) => {
        if (response && response.data && response.data.success) {
          setTitle("");
          setContent("");
          setTotalPosts(response.data.count);
          setPosts([...(posts || []), response.data.payload]);
          setIsLoading(false);
        }
      })
      .catch(console.warn);
  };

  useEffect(() => {
    const fetchFeeds = async () => {
      await axios
        .get(`http://localhost:8080/?page=${page}&limit=5`)
        .then((response) => {
          console.log("response1", response);
          if (response && response.data && response.data.success) {
            setPosts(response.data.payload);
            setIsLoading(false);
          }
        })
        .catch(console.error);
    };
    fetchFeeds();
  }, []);

  const fetchMoreData = () => {
    setPage(page + 1);
    const fetchFeeds = async () => {
      await axios
        .get(`http://localhost:8080/?page=${page}&limit=5`)
        .then((response) => {
          console.log("response1", response);
          if (response && response.data && response.data.success) {
            setPosts(posts?.concat(response.data.payload));
            setIsLoading(false);
          }
        })
        .catch(console.error);
    };
    fetchFeeds();
  };
  return (
    <div>
      <h1 className="mt-3">Assignment</h1>
      <Container>
        <Form className="mb-3">
          <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
            <Form.Label>Title</Form.Label>
            <Form.Control
              value={title}
              type="text"
              placeholder="Enter Title"
              onChange={(e) => handleOnChange(e, "title")}
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
            <Form.Label>Content</Form.Label>
            <Form.Control
              onChange={(e) => handleOnChange(e, "content")}
              required
              value={content}
              as="textarea"
              rows={3}
            />
          </Form.Group>
          <Button onClick={onSubmit} variant="warning">
            Submit Content
          </Button>{" "}
        </Form>
      </Container>
      <Container>
        <h1>Posts</h1>
      </Container>
      <Container>
        <InfiniteScroll
          dataLength={posts?.length || 0}
          next={fetchMoreData}
          hasMore={posts?.length !== totalPosts}
          loader={isLoading && <Spinner animation="grow" variant="warning" />}
        >
          <Stack gap={4} className="col-md-4 mx-auto">
            {!isLoading &&
              posts &&
              posts.map((post) => (
                <Card style={{ width: "28rem" }}>
                  <Card.Body>
                    <Card.Title>{post.title}</Card.Title>
                    <Card.Text>{post.content}</Card.Text>
                  </Card.Body>
                </Card>
              ))}
          </Stack>
        </InfiniteScroll>
      </Container>
    </div>
  );
};

export default Home;
