import React, { useCallback, useState } from "react";
import {
  Button,
  Card,
  Container,
  Form,
  Placeholder,
  Stack,
} from "react-bootstrap";
import InfiniteScroll from "react-infinite-scroll-component";

export default function Home() {
  const [inputValues, setInputValues] = useState({
    title: "",
    content: "",
  });

  const [title, setTitle] = useState();
  const [content, setContent] = useState();

  const handleOnChange = useCallback(
    (event, prop) => {
      console.log("Hejj");
      console.log("prop: ", prop);

      console.log("event: ", event);
      console.log("event.target: ", event.target);
      console.log("event.target.value: ", event.target.value);

      const { name, value } = event.target;
      setInputValues({ ...inputValues, [name]: value });

      if (prop === "title") {
        console.log("TITLE");
        setTitle(event.target.value);
      }
      if (prop === "content") {
        console.log("CONTENT");
        setContent(event.target.value);
      }
    },
    [inputValues, title, content]
  );

  const fetchMoreData = () => {};
  return (
    <div>
      {/* <Form.Input
        fluid
        required
        value={form.password}
        type="password"
        label="Password"
        placeholder="••••••••••"
        autoComplete="off"
        onChange={this._onChangeProp.bind(this, "password")}
      /> */}
      <h1 className="mt-3">Assignment</h1>
      <Container>
        <Form className="mb-3">
          <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
            <Form.Label>Title</Form.Label>
            <Form.Control
              value={title}
              type="text"
              placeholder="Enter Title"
              onChange={(e) => handleOnChange(e, "title")}
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
            <Form.Label>Content</Form.Label>
            <Form.Control
              onChange={(e) => handleOnChange(e, "content")}
              required
              value={content}
              as="textarea"
              rows={3}
            />
          </Form.Group>
          <Button variant="warning">Submit Content</Button>{" "}
        </Form>
      </Container>
      <Container>
        <h1>Posts</h1>
      </Container>
      <Container>
        <InfiniteScroll
          dataLength={8}
          next={fetchMoreData}
          hasMore={true}
          loader={<h4>Loading...</h4>}
        >
          {/* {this.state.items.map((i, index) => (
            <div style={style} key={index}>
              div - #{index}
            </div>
          ))} */}
          <Stack gap={4} className="col-md-4 mx-auto">
            <Card style={{ width: "28rem" }}>
              <Card.Body>
                <Card.Title>Card Title</Card.Title>
                <Card.Text>
                  Some quick example text to build on the card title and make up
                  the bulk of the card's content.
                </Card.Text>
              </Card.Body>
            </Card>
          </Stack>
          {/* <Card style={{ width: "18rem" }}>
            <Card.Body>
              <Placeholder as={Card.Title} animation="glow">
                <Placeholder xs={6} />
              </Placeholder>
              <Placeholder as={Card.Text} animation="glow">
                <Placeholder xs={7} /> <Placeholder xs={4} />{" "}
                <Placeholder xs={4} /> <Placeholder xs={6} />{" "}
                <Placeholder xs={8} />
              </Placeholder>
              <Placeholder.Button variant="primary" xs={6} />
            </Card.Body>
          </Card> */}
        </InfiniteScroll>
      </Container>
    </div>
  );
}
